package loop;

public class Ascii {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int ch1 = 'a'; ch1 < 'z'; ch1++) {

			System.out.println("Print " + (char) ch1 + " =" + ch1);
		}

		for (int ch1 = 'A'; ch1 < 'Z'; ch1++) {

			System.out.println("Print " + (char) ch1 + " =" + ch1);
		}
	}

}
